import mockData from"../../../data/data.js";import{select}from"../settings.js";import Component from"./component.js";class Media extends Component{constructor(t,e,i=""){super(t,select.templateOf.media),this.endpoint=i,this.eventToEmit=e,this.initialize()}async getData(){return mockData}async initialize(){var t=await this.getData();this.render(t),this.initActions()}initActions(){const e=this.wrapper.querySelector("button");e.addEventListener("click",()=>{var t=new Event(this.eventToEmit,{bubbles:!0});e.dispatchEvent(t)})}}export default Media;